  /******************************************************************************
   * Name:    CRC-8               x8+x2+x+1
   * Poly:    0x07
   * Init:    0x00
   * Refin:   False
   * Refout:  False
   * Xorout:  0x00
   * Note: 高亮指定代码行数
   *****************************************************************************/
  uint8_t crc8(uint8_t *data, uint32_t length)
  {
      uint8_t i;
      uint8_t crc = 0;        // Initial value
      while(length--)
      {
          crc ^= *data++;        // crc ^= *data; data++;
          for ( i = 0; i &lt; 8; i++ )
          {
              if ( crc &amp; 0x80 )
                  crc = (crc &lt;&lt; 1) ^ 0x07;
              else
                  crc &lt;&lt;= 1;
          }
      }
      return crc;
  }
